import urllib2from django.utils import simplejson as jsondef getTweets(coords):    query = "geocode="+coords+",100km"+"&rpp=20"    url = "http://search.twitter.com/search.json?"+query        try:        webpage = urllib2.urlopen(url)    except:        content = ""    else:        content = webpage.read()            return content    def parseTweets(rawTweets):    parsed = []        if rawTweets != "":        obj = json.loads(rawTweets)        results = obj["results"]                if results != []:            for tweet in results:                location = tweet["location"]                profilepic = tweet["profile_image_url"]                createdate = tweet["created_at"]                user = tweet["from_user"]                text = tweet["text"].replace("RT", "")                single = (location, profilepic, createdate, user, text)                parsed.append(single)        return parsed        